statements.insert:
IF EXISTS ( SELECT 1 FROM UCD.SALES_PRICE WHERE PRODUCT_ID = #PRODUCT_ID# AND BEG_DATE < #BEG_DATE# ) BEGIN UPDATE UCD.SALES_PRICE SET END_DATE = CONVERT(VARCHAR(8), DATEADD(DAY, -1, CONVERT(DATETIME, #BEG_DATE#, 112)), 112) , UPD_TIME = GETDATE() , UPD_USER = #INS_USER# WHERE PRODUCT_ID = #PRODUCT_ID# AND BEG_DATE = ( SELECT MAX(BEG_DATE) FROM UCD.SALES_PRICE WHERE PRODUCT_ID = #PRODUCT_ID# AND BEG_DATE < #BEG_DATE# ) END;
INSERT INTO UCD.SALES_PRICE ( PRODUCT_ID, BEG_DATE, END_DATE, SALE_PRICE, REMARKS, INS_TIME, INS_USER ) VALUES ( #PRODUCT_ID#, #BEG_DATE#, '29991231', #SALE_PRICE#, #REMARKS#, GETDATE(), #INS_USER# )
SELECT @@IDENTITY AS REG_ID
